import React, { useState, useEffect } from 'react';

function App() {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/api/devices')
      .then(res => res.json())
      .then(setDevices)
      .catch(() => setDevices([]));
  }, []);

  return (
    <div style={{padding: '32px', background: '#282c34', minHeight: '100vh', color: '#fff'}}>
      <h1>USB Storage Device Monitor</h1>
      <table style={{width: '100%', background: '#333', marginTop: 20, borderCollapse: 'collapse'}}>
        <thead>
          <tr>
            <th>Device ID</th>
            <th>Name</th>
            <th>Status</th>
            <th>Path</th>
          </tr>
        </thead>
        <tbody>
          {devices.map(dev => (
            <tr key={dev.deviceId}>
              <td>{dev.deviceId}</td>
              <td>{dev.friendlyName}</td>
              <td>{dev.isAllowed ? "Allowed" : "Denied"}</td>
              <td>{dev.devicePath}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
