import React, { useEffect, useState } from 'react';

function App() {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    fetch('/api/devices')
      .then(res => res.json())
      .then(setDevices)
      .catch(() => setDevices([]));
  }, []);

  return (
    <div style={{ padding: '20px', fontFamily:'Arial', background: '#222', minHeight: '100vh', color:'#eee' }}>
      <h2>USB Device Monitoring Dashboard</h2>
      <table style={{width:'100%', borderCollapse:'collapse'}}>
        <thead>
          <tr>
            <th style={{border: '1px solid #777', padding: '8px'}}>Device ID</th>
            <th style={{border: '1px solid #777', padding: '8px'}}>Name</th>
            <th style={{border: '1px solid #777', padding: '8px'}}>Status</th>
            <th style={{border: '1px solid #777', padding: '8px'}}>Path</th>
          </tr>
        </thead>
        <tbody>
          {devices.map(device => (
            <tr key={device.deviceId}>
              <td style={{border: '1px solid #777', padding: '8px'}}>{device.deviceId}</td>
              <td style={{border: '1px solid #777', padding: '8px'}}>{device.friendlyName}</td>
              <td style={{border: '1px solid #777', padding: '8px'}}>{device.isAllowed ? 'Allowed' : 'Blocked'}</td>
              <td style={{border: '1px solid #777', padding: '8px'}}>{device.devicePath}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
